mapper.select:

SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT *
        FROM MEMBER
        ORDER BY MEMBER_ID
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rnum > #{offset}


SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT *
        FROM MEMBER
        WHERE MEMBER_NAME LIKE '%' || #{name} || '%'
        ORDER BY MEMBER_ID
    ) a
    WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rnum > #{offset}

mapper.update:
UPDATE MEMBER SET MEMBER_NAME = #{memberName}, MEMBER_PASSWORD = #{memberPassword}, MEMBER_GENDER = #{memberGender}, MEMBER_PHONE = #{memberPhone}, MEMBER_EMAIL = #{memberEmail}, MEMBER_COUPON = #{memberCoupon}, MEMBER_ADMIN =
'Y' 'N'
, MEMBER_STAMP = #{memberStamp} WHERE MEMBER_ID = #{memberId}
