mapper.select:
SELECT MOVIE_NO, MOVIE_TITLE, MOVIE_IMAGE, MOVIE_AGE_LIMIT FROM MOVIE M JOIN CINEMA C ON M.MOVIE_NO = C.CINEMA_MOVIE_NO LEFT JOIN THEATER T ON C.CINEMA_NO = T.THEATER_CINEMA_NO LEFT JOIN TICKET TK ON C.CINEMA_NO = TK.TICKET_CINEMA_NO WHERE TO_CHAR(C.CINEMA_SCREEN_DATE,'YYYYMMDD') BETWEEN TO_CHAR(SYSDATE, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')+14
AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE
AND M.MOVIE_NO = #{movieNo}
AND C.CINEMA_BLG = #{cinemaBLG}
AND TO_CHAR(C.CINEMA_SCREEN_DATE, 'YYYY-MM-DD') = #{cinemaScreenDate}
GROUP BY MOVIE_NO, MOVIE_TITLE, MOVIE_IMAGE, MOVIE_AGE_LIMIT ORDER BY (SUM(NVL(TK.TICKET_TEEN, 0)) + SUM(NVL(TK.TICKET_ADULT, 0)) + SUM(NVL(TK.TICKET_SENIOR, 0))) DESC

SELECT CINEMA_RLG, CINEMA_BLG FROM CINEMA C JOIN THEATER T ON C.CINEMA_NO = T.THEATER_CINEMA_NO WHERE CINEMA_MOVIE_NO IN ( SELECT C.CINEMA_MOVIE_NO FROM MOVIE M JOIN CINEMA C ON M.MOVIE_NO = C.CINEMA_MOVIE_NO JOIN THEATER T ON C.CINEMA_NO = T.THEATER_CINEMA_NO WHERE TO_CHAR(C.CINEMA_SCREEN_DATE,'YYYYMMDD') BETWEEN TO_CHAR(SYSDATE, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')+14
AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE
AND M.MOVIE_NO = #{movieNo}
AND C.CINEMA_BLG = #{cinemaBLG}
AND TO_CHAR(C.CINEMA_SCREEN_DATE, 'YYYY-MM-DD') = #{cinemaScreenDate}
AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE
GROUP BY MOVIE_NO )
AND TO_CHAR(CINEMA_SCREEN_DATE, 'YYYY-MM-DD') = #{cinemaScreenDate}
AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE
GROUP BY CINEMA_RLG, CINEMA_BLG

		SELECT CINEMA_SCREEN_DATE
		FROM CINEMA C
		JOIN THEATER T ON C.CINEMA_NO = T.THEATER_CINEMA_NO
		WHERE CINEMA_MOVIE_NO IN (
			SELECT C.CINEMA_MOVIE_NO
			FROM MOVIE M
            JOIN CINEMA C ON M.MOVIE_NO = C.CINEMA_MOVIE_NO
            JOIN THEATER T ON C.CINEMA_NO = T.THEATER_CINEMA_NO
			WHERE TO_CHAR(C.CINEMA_SCREEN_DATE,'YYYYMMDD')
			BETWEEN TO_CHAR(SYSDATE, 'YYYYMMDD') AND TO_CHAR(SYSDATE, 'YYYYMMDD')+14

			AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE 


				 AND M.MOVIE_NO = #{movieNo}



				 AND C.CINEMA_BLG = #{cinemaBLG}



				AND TO_CHAR(C.CINEMA_SCREEN_DATE, 'YYYY-MM-DD') = #{cinemaScreenDate}

			GROUP BY MOVIE_NO
		)

			AND CINEMA_BLG = #{cinemaBLG}


			AND TO_CHAR(CINEMA_SCREEN_DATE, 'YYYY-MM-DD') = #{cinemaScreenDate}


			AND T.THEATER_START_TIME > SYSDATE + INTERVAL '10' MINUTE 

		GROUP BY CINEMA_SCREEN_DATE
